import React, { Component } from 'react'

class UpdateBreederProfileForm extends Component {

    state = {
        edit: false,
        editName: false,
        editAddress: false,
        editPhone: false,
        editEmail: false,
        name: this.props.currentLogin.name,
        address: this.props.currentLogin.address,
        phone: this.props.currentLogin.phone,
        email: this.props.currentLogin.email,
        newName: "",
        newAddress: "",
        newPhone: "",
        newEmail: "",
    }

    toggleEdit = (e) => {
        e.preventDefault()
        this.setState({
            [e.target.name]: !this.state.edit,
        })
    }

    toggleOff = (e) => {
        // debugger
        console.log(e.target.className)
        this.state.edit !== false
            && e.target.className !== "inputText"
            && this.setState({
            [e.target.name]: !this.state.edit,
            })
    }

    handleSubmit = (e) => {

        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        let breeder = this.props.currentLogin
        return (
            <div>
{/* NAME */}
            {this.state.edit
            ? <form onSubmit={(e) => this.props.updateNote(e, this.state.newName)}>
                <input type="text" className="inputText"
                    defaultValue={this.state.name}
                    onChange={(e) => this.handleUpdatedNote(e.target.value)}
                ></input>
            </form>
            : <h2 className={"name"} onClick={(e) => this.toggleEdit(e)}>{this.state.name}</h2>}
{/* ADDRESS */}
            {this.state.edit
            ? <form onSubmit={(e) => this.props.updateNote(e, this.state.newNote)}>
                <input type="text" className="inputText"
                    defaultValue={this.state.address}
                    onChange={(e) => this.handleUpdatedNote(e.target.value)}
                ></input>
            </form>
            : <h2 className={"name"} onClick={(e) => this.toggleEdit(e)}>{this.state.name}</h2>}
{/* PHONE */}
                <h2>{breeder.name}</h2>
                <p>{breeder.address}</p>
                <p>{breeder.phone}</p>

{/* EMAIL */}
                <p>{breeder.email}</p>
                <p>{breeder.password}</p>
            </div>
        )
    }
}

export default UpdateBreederProfileForm
