import React, { Component } from 'react'

class UpdateBreederProfileForm extends Component {

    state = {
        edit: false,
        name: this.props.currentLogin.name,
        address: this.props.currentLogin.address,
        phone: this.props.currentLogin
    }

    toggleEdit = (e) => {
        e.preventDefault()
        this.setState({
            edit: !this.state.edit,
        })
    }

    toggleOff = (e) => {
        // debugger
        console.log(e.target.className)
        this.state.edit !== false
            && e.target.className !== "inputText"
            && this.setState({
                edit: !this.state.edit,
            })
    }

    render() {
        let breeder = this.props.currentLogin
        return (
            <div>

            {this.state.edit
                    ? <form onSubmit={(e) => this.props.updateNote(e, this.state.newNote, this.props.note)}>
                        <input
                            type="text"
                            defaultValue={this.state.note}
                            //    name="updatedNote" 
                            className="inputText"
                            //    value={this.state.updatedNote}
                            onChange={(e) => this.handleUpdatedNote(e.target.value)}
                        ></input>
                    </form>
                    : <h2 className={this.state.note} onClick={(e) => this.toggleEdit(e)}>{this.state.note}</h2>}


                <h2>{breeder.name}</h2>
                <p>{breeder.address}</p>
                <p>{breeder.phone}</p>
                <p>{breeder.email}</p>
                <p>{breeder.password}</p>
            </div>
        )
    }
}

export default UpdateBreederProfileForm
