import React, { Component } from "react";

export default class UserLogin extends Component {
    state = {
        userName: "",
        userPassword: "",
        userClickedSignup: false,
        sellerClickedSignup: false,
    }



    handleLogin = (e) => {
        e.preventDefault()
        console.log(e.target[0].value)
        console.log(e.target[1].value)
        fetch("http://localhost:3000/userlogin", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                name: e.target[0].value,
                password: e.target[1].value
            })
        })
        .then(res => res.json())
        .then(user => {

            localStorage.token = user.token
        })

        // this.props.history.push("/userhomepage")
    }

    handleRegister = () => {
        this.props.history.push("/signup")
    }

    toggleUserSignup = () => {
        this.setState({
            userClickedSignup: !this.state.userClickedSignup
        })
    }
    toggleSellerSignup = () => {
        this.setState({
            sellerClickedSignup: !this.state.sellerClickedSignup
        })
    }


    render() {
        return (
            <div>




                {/* <Navbar parentName={document.title} /> */}

            <div class="float-container">

{/* USER */}
                <div class="float-child">
               {this.state.userClickedSignup 
                ?<div>
                    <h3> please enter info  </h3>
                    <form>
                        <input type="text" name="username" value=""
                        placeholder="Enter your Username"/>
                        <br/>
                        <input 
                        type="password" name="password" value=""
                        placeholder="Enter your Passwords"/>
                        <br/>
                        <input 
                        type="text" name="address" value="" 
                        placeholder="Enter your Address"/>
                        <br/>
                        <input 
                        type="text" name="phone" value=""
                        placeholder="Enter your Phone"/>
                        <br/>
                        <input 
                        type="text" name="email" value=""
                        placeholder="Enter your Email"/>
                        <br/>
                        <button>Submit </button>
                    </form>
                </div>

                :<div>
                    <h1>User sign-in</h1>
                    <form onSubmit={ (e) => this.handleLogin(e) }>
                        <input 
                        type="text" name="username"
                        value={this.state.userName}
                        placeholder="Enter your username"
                        onChange={(e) => this.setState({
                            userName: e.target.value
                        })}/>
                    <br/>
                        <input 
                        type="password" name="password"
                        value={this.state.userPassword}
                        placeholder="Enter your passwords"
                        onChange={(e) => this.setState({
                            userPassword: e.target.value
                        })}/>
                    <br/>
                    <button> Login </button>
                </form>
                <button onClick={() => this.toggleUserSignup()}>Signup</button>
                    </div>}
                    </div>

{/* SELLER */}  
<div class="float-child">
               {this.state.sellerClickedSignup 
                ?<div>
                    <h3> please enter info  </h3>
                    <form>
                        <input type="text" name="username" value=""
                        placeholder="Enter your Username"/>
                        <br/>
                        <input 
                        type="password" name="password" value=""
                        placeholder="Enter your Passwords"/>
                        <br/>
                        <input 
                        type="text" name="address" value="" 
                        placeholder="Enter your Address"/>
                        <br/>
                        <input 
                        type="text" name="phone" value=""
                        placeholder="Enter your Phone"/>
                        <br/>
                        <input 
                        type="text" name="email" value=""
                        placeholder="Enter your Email"/>
                        <br/>
                        <button>Submit </button>
                    </form>
                </div>

                :<div>
                    <h1>Seller sign-in</h1>
                    <form onSubmit={ (e) => this.handleLogin(e) }>
                        <input 
                        type="text" name="username"
                        value={this.state.userName}
                        placeholder="Enter your username"
                        onChange={(e) => this.setState({
                            userName: e.target.value
                        })}/>
                    <br/>
                        <input 
                        type="password" name="password"
                        value={this.state.userPassword}
                        placeholder="Enter your passwords"
                        onChange={(e) => this.setState({
                            userPassword: e.target.value
                        })}/>
                    <br/>
                    <button> Login </button>
                </form>
                <button onClick={() => this.toggleSellerSignup()}>Signup</button>
                    </div>}
                    </div>
            </div>








            </div>
        )
    }
}
