import React, { PureComponent } from 'react'
import Navbar from "./Navbar"
// import Navbar from "./Component/Navbar"
import DogContainer from '../Containers/SavedDogsCard'


class BreederHomePage extends PureComponent {

    state = {
        dogs:this.props.currentLogin.pets,
        expandContainer:false,
        itemsToShow: 4
    }

    showMore() {
        this.state.itemsToShow === 3 ? 
            this.setState({ itemsToShow: this.state.dogs.length, expandContainer:true})
         : 
            this.setState({ itemsToShow: 4, expandContainer:false})

        // show more entries
        // switch to "show less"
       }

    render() {
        this.showMore = this.showMore.bind(this)

        return (
            <div>
                <Navbar />
                <div id="flex-inline-block">
                    <div class="saved-dogs">
                        <h1>manage dogs</h1>
                        {/* {this.state.currentLogin.pets.slice(0, this.state.itemsToShow).map(dog =><DogContainer dog={dog} key={dog.id}/>)} */}
                        {/* {this.props.dogs.map(dog =><DogContainer dog={dog} key={dog.id}/>)} */}
                    <br></br>
                    <a onClick={this.showMore}>
                    {this.state.expandContainer 
                    ? (<button>show more</button>)
                    : (<button>show less</button>)}
                    </a>

                    </div>
                    <div class="float-child">
                        <h1>view requests</h1>
                    </div>
                </div>
            </div>
        )
    }
}

export default BreederHomePage
